import React, { useState, useEffect }  from 'react';
import { Link } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive';

function Home() {

  const [userName, setUserName] = useState('Dheeresha');

  const isDesktopOrLaptop = useMediaQuery({ query: '(min-width: 1824px)' });
  const isPortriat = useMediaQuery({ query: '(orientation: portrait)'})
  const isRetina = useMediaQuery({ query: '(min-resolution: 2dppx)' })
  const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })

  useEffect(() => {
    console.log('Home Page is now loaded');
    console.log(userName);
    setUserName('Dheeresha Shettigar');
  },[userName]);

  return(
    <div> 
      <span>Home Page</span>
      <br/>
      <Link to="/signup">Signup</Link>
      <br/>
      <h1>Device Test!</h1>
      {isDesktopOrLaptop && <p>You are a desktop or laptop</p>}
      {isBigScreen && <p>You  have a huge screen</p>}
      {isTabletOrMobile && <p>You are a tablet or mobile phone</p>}
      <p>Your are in {isPortrait ? 'portrait' : 'landscape'} orientation</p>
      {isRetina && <p>You are retina</p>}
      {isPortriat && <p> You are In portrait mode</p>}
    </div>
  );
}

export default Home;
